definition.script:
def maven = docker.image("maven:3.3.3-jdk-8")

node {
   stage 'Build'
   git 'https://github.com/malston/cddemo'
   maven.inside {
      sh 'mvn clean package -Dmaven.repo.local=.repo'
   }

   stage 'Bake Docker image'
   def image = docker.build("malston/demo:${env.BUILD_TAG}", '.')

   stage 'Test image'
   def app = image.run('-p 9999:8080')
   try {
      maven.inside {
         sh 'mvn -f sometests test -Durl=http://172.17.42.1:9999/x -Dmaven.repo.local=.repo'
      }
   } finally {
      app.stop()
   }

   checkpoint 'before-push'

   docker.withRegistry('http://localhost:5000', 'docker-registry') {
       // image.push("latest")
   }


}
